<?php

namespace AppBundle\Entity;

/**
 * ProjectRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProjectRepository extends \Doctrine\ORM\EntityRepository
{
    public function getProjectStat()
    {
        $qb = $this->getEntityManager()->createQueryBuilder('project');
        $qb
            ->select('project')
            ->from('AppBundle:Project', 'project')
            ->addSelect('COUNT(user.id) as countLikes')
            ->leftJoin('project.likedUsers', 'user')
            ->groupBy('project.id')
            ->orderBy("countLikes", 'DESC')

            ->getQuery()
            ->getResult()
        ;
        $query = $qb->getQuery();
        $results = $query->getResult();
        return $results;
    }

    public function getProjectShow()
    {
        $qb = $this->getEntityManager()->createQueryBuilder('project');
        $qb
            ->select('project')
            ->from('AppBundle:Project', 'project')
            ->addSelect('COUNT(user.id) as countLikes')
            ->leftJoin('project.likedUsers', 'user')
            ->andWhere('project.approved = :approved')
            ->setParameter('approved', true)
            ->groupBy('project.id')

            ->getQuery()
            ->getResult()
        ;
        $query = $qb->getQuery();
        $results = $query->getResult();
        return $results;
    }

    public function getOneProjectShow($id)
    {
        $qb = $this->getEntityManager()->createQueryBuilder('project');
        $qb
            ->select('project')
            ->from('AppBundle:Project', 'project')
            ->addSelect('COUNT(user.id) as countLikes')
            ->leftJoin('project.likedUsers', 'user')
            ->andWhere('project.approved= :approved')
            ->andWhere('project.id = :id')
            ->setParameter('approved', true)
            ->setParameter('id', $id)
            ->groupBy('project.id')

            ->getQuery()
            ->getResult()
        ;
        $query = $qb->getQuery();
        $results = $query->getResult();
        return $results;
    }
}
